import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById('root')
);

class SubmitBtn extends React.Component {
  render() {
    return (
      <button className="submit_data">
        {"Press This Button to submit data"}
      </button>
    );
  }
}

class FrontPage extends React.Component {
  constructor(props){
    super(props);
    this.state = {value:''};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  renderBtn(i) {
    return <SubmitBtn />;
  }

  handleChange(event){
    this.setState({value: event.target.value});
  }

  handleSubmit(event) {
    alert('A name was submitted: ' + this.state.value);
    event.preventDefault();
  }

  render() {
    const stomach_pressure = 'Enter baby stomach pressure:';
    const feed_flow_rate = 'Enter feed flow rate:';

    return (
      <div>
        <br></br>
        <div className="stomach-pressure">{stomach_pressure}</div><br></br>
        <div className="flow-rate">{feed_flow_rate}</div><br></br>
        <div className="row">
          {this.renderBtn()}
        </div>
        <form onSubmit={this.handleSubmit}>
        <label>
          Name:
          <input className="stomach-pressure" type="text" value={this.state.value} onChange={this.handleChange} />
        </label>
        <label>
          Name:
          <input type="text" value={this.state.value} onChange={this.handleChange} />
        </label>
        <input type="submit" value="Submit" />
      </form>
      </div>
    );
  }
}

// ========================================

ReactDOM.render(
  <FrontPage />,
  document.getElementById('root')
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
